import React, { useEffect, useState } from 'react';
import { useAuthContext } from '../hooks/useAuthContext';
import { useGroupsContext } from '../hooks/useGroupContext';
import { createGroup } from '../Functions';

const Dashboard = () => {
  const { user } = useAuthContext();
  const { groups, dispatch } = useGroupsContext();

  const [name, setName] = useState('');
  const [error, setError] = useState(null);
  const [groupItems, setGroupItems] = useState({});

  useEffect(() => {
    const fetchGroups = async () => {
      if (!user) return;

      try {
        const response = await fetch(`/api/users/${user._id}/groups/`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json'
          }
        });

        const json = await response.json();

        if (response.ok) {
          dispatch({ type: 'SET_GROUPS', payload: json });

          // Fetch items for each group
          for (let group of json) {
            fetchGroupItems(group._id);
          }
        } else {
          console.error('Failed to fetch groups:', json);
        }
      } catch (err) {
        console.error('Error fetching groups:', err);
      }
    };

    fetchGroups();
  }, [user, dispatch]);

  const fetchGroupItems = async (groupId) => {
    try {
      const response = await fetch(`/api/groups/${groupId}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json'
        }
      });

      const group = await response.json();

      if (response.ok) {
        setGroupItems((prevItems) => ({
          ...prevItems,
          [groupId]: group.items
        }));
      } else {
        console.error('Failed to fetch group items:', group);
      }
    } catch (err) {
      console.error('Error fetching group items:', err);
    }
  };



  const deleteGroup = async (groupId) => {
    try {
      const response = await fetch(`/api/groups/${groupId}`, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json'
        }
      });

      const json = await response.json();

      if (response.ok) {
        console.log('Group deleted:', json);
        dispatch({ type: 'DELETE_GROUP', payload: groupId });
        setError(null);
        setGroupItems((prevItems) => {
          const newItems = { ...prevItems };
          delete newItems[groupId];
          return newItems;
        });
      } else {
        setError(json.message);
      }
    } catch (err) {
      console.error('Error deleting group:', err);
      setError('An error occurred while deleting the group.');
    }
  };

  return (
    <div>
      <h1>Dashboard</h1>
      <h2>Groups</h2>
      {user && <p>User ID: {user._id}</p>}
      <ul>
        {groups && groups.length > 0 ? (
          groups.map((group) => (
            <li key={group._id}>
              <div>
                <span>{group.name}</span>
                <button onClick={() => deleteGroup(group._id)}>Delete</button>
              </div>
              <ul>
                {groupItems[group._id] && groupItems[group._id].length > 0 ? (
                  groupItems[group._id].map((item) => (
                    <li key={item._id}>{item.name} - ${item.price}</li>
                  ))
                ) : (
                  <li>No items in this group</li>
                )}
              </ul>
            </li>
          ))
        ) : (
          <li>No groups available</li>
        )}
      </ul>
      <form onSubmit={createGroup}>
        <label>
          Create new group
        </label>
        <input
          type="text"
          onChange={(e) => setName(e.target.value)}
          value={name}
        />
        <button type="submit">Create Group</button>
      </form>
      {error && <div className="error">{error}</div>}
    </div>
  );
};

export default Dashboard;
