import React, { useEffect, useState } from 'react'
import { useAuthContext } from '../hooks/useAuthContext'
import { useFetchUserGroups } from './useFetchUserGroups';


function Dashboard() {

  const userGroups = useFetchUserGroups();
  const [groupItems, setGroupItems] = useState([]);

  useEffect(() => {
    const fetchGroupItems = async () => {
      const itemsPromises = userGroups.map(async (group) => {
        const response = await fetch(`/api/groups/${group._id}`);
        const groupData = await response.json();
        return groupData.items;
      });

      const allItems = await Promise.all(itemsPromises);
      setGroupItems(allItems.flat());
    };

    if (userGroups.length > 0) {
      fetchGroupItems();
    }
  }, [userGroups]);

  return (
    <div>
      Dashboard
    </div>
  )
}

export default Dashboard
