import React from 'react';
import Drawer from '@mui/material/Drawer';
import List from '@mui/material/List';
import Box from '@mui/material/Box';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import GroupIcon from '@mui/icons-material/Group';
import Divider from '@mui/material/Divider';

const item = {
  py: '2px',
  px: 3,
  color: 'rgba(255, 255, 255, 0.7)',
  '&:hover, &:focus': {
    bgcolor: 'rgba(255, 255, 255, 0.08)',
  },
};

const itemCategory = {
  boxShadow: '0 -1px 0 rgb(255,255,255,0.1) inset',
  py: 1.5,
  px: 3,
};

const drawer = {
  marginTop: '64px', // Adjust the value based on the height of your navbar
};

export default function Navigator({ groups, onGroupSelect, ...other }) {
  return (
    <Drawer variant="permanent" style={drawer} {...other}>
      <List disablePadding>
        <ListItem sx={{ ...item, ...itemCategory, fontSize: 22, color: '#fff' }}>
          Your Groups
        </ListItem>
        <Divider />
        {groups && groups.length > 0 ? (
          groups.map((group) => (
            <Box key={group._id} sx={{ bgcolor: '#101F33' }}>
              <ListItem disablePadding>
                <ListItemButton sx={item} onClick={() => onGroupSelect(group._id)}>
                  <ListItemIcon>
                    <GroupIcon />
                  </ListItemIcon>
                  <ListItemText>{group.name}</ListItemText>
                </ListItemButton>
              </ListItem>
              <Divider sx={{ mt: 2 }} />
            </Box>
          ))
        ) : (
          <ListItem>
            <ListItemText>No groups available</ListItemText>
          </ListItem>
        )}
      </List>
    </Drawer>
  );
}
