import React, { useEffect, useState } from 'react';
import { useAuthContext } from '../hooks/useAuthContext';
import { useGroupsContext } from '../hooks/useGroupsContext';

const Dashboard = () => {
  const { user } = useAuthContext();
  const { groups, dispatch } = useGroupsContext();

  const [name, setName] = useState('');
  const [items, setItems] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchGroups = async () => {
      if (!user) return;

      try {
        const response = await fetch(`/api/users/${user._id}/groups/`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json'
          }
        });

        const json = await response.json();

        if (response.ok) {
          dispatch({ type: 'SET_GROUPS', payload: json });
        } else {
          console.error('Failed to fetch groups:', json);
        }
      } catch (err) {
        console.error('Error fetching groups:', err);
      }
    };

    fetchGroups();
  }, [user, dispatch]);

  const createGroup = async (e) => {
    e.preventDefault();

    if (!name) {
      setError('Group name is required');
      return;
    }

    const group = {
      name,
      items
    };

    try {
      const response = await fetch(`/api/groups/${user._id}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(group)
      });

      const json = await response.json();

      if (response.ok) {
        setName('');
        setItems([]);
        console.log('New group created:', json);
        dispatch({ type: 'CREATE_GROUP', payload: json });
        setError(null);
      } else {
        setError(json.message);
      }
    } catch (err) {
      console.error('Error creating group:', err);
      setError('An error occurred while creating the group.');
    }
  };

  const deleteGroup = async (groupId) => {
    try {
      const response = await fetch(`/api/groups/${groupId}`, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json'
        }
      });

      const json = await response.json();

      if (response.ok) {
        console.log('Group deleted:', json);
        dispatch({ type: 'DELETE_GROUP', payload: groupId });
        setError(null);
      } else {
        setError(json.message);
      }
    } catch (err) {
      console.error('Error deleting group:', err);
      setError('An error occurred while deleting the group.');
    }
  };

  return (
    <div>
      <h1>Dashboard</h1>
      <h2>Groups</h2>
      {user && <p>User ID: {user._id}</p>}
      <ul>
        {groups && groups.length > 0 ? (
          groups.map((group) => (
            <li key={group._id}>
              {group.name}
              <button onClick={() => deleteGroup(group._id)}>Delete</button>
            </li>
          ))
        ) : (
          <li>No groups available</li>
        )}
      </ul>
      <form onSubmit={createGroup}>
        <label>
          Create new group
        </label>
        <input
          type="text"
          onChange={(e) => setName(e.target.value)}
          value={name}
        />
        <button type="submit">Create Group</button>
      </form>
      {error && <div className="error">{error}</div>}
    </div>
  );
};

export default Dashboard;
