import React, { useState, useEffect, useCallback } from 'react';
import { useGroupsContext } from '../hooks/useGroupContext';
import useFetchGroups from '../hooks/useFetchUserGroups';
// import useDeleteGroup from '../hooks/useDeleteGroup';
import useAddItemToGroup from '../hooks/useAddItemToGroup';
import useRemoveItemFromGroup from '../hooks/useRemoveItemFromGroup';
import useFetchGroupItems from '../hooks/useFetchGroupItems';

import { Box, Typography, TextField, Button, List, ListItem, ListItemText, ListItemSecondaryAction, IconButton, Grid, Paper } from '@mui/material';
import RemoveCircleIcon from '@mui/icons-material/RemoveCircle';
import AddIcon from '@mui/icons-material/Add';
import GroupNav from '../components/GroupNav';
import DashboardHeader from '../components/DashboardHeader';
import Members from '../components/Members';

const Dashboard = () => {
  const { groups } = useGroupsContext();
  const [selectedGroupId, setSelectedGroupId] = useState(null);
  const [newItemName, setNewItemName] = useState('');
  const [newItemPrice, setNewItemPrice] = useState('');

  useFetchGroups();
  // const { deleteGroup } = useDeleteGroup();
  const { addItemToGroup } = useAddItemToGroup();
  const { removeItemFromGroup } = useRemoveItemFromGroup();

  const { items: groupItems, members: groupMembers } = useFetchGroupItems(selectedGroupId);

  useEffect(() => {
    if (groups.length > 0 && !selectedGroupId) {
      setSelectedGroupId(groups[0]._id);
    }
  }, [groups, selectedGroupId]);

  const handleAddItem = useCallback(async () => {
    const result = await addItemToGroup(selectedGroupId, newItemName, newItemPrice);

    if (result) {
      setNewItemName('');
      setNewItemPrice('');
    }
  }, [selectedGroupId, newItemName, newItemPrice, addItemToGroup]);

  const handleRemoveItem = useCallback(async (itemId) => {
    await removeItemFromGroup(selectedGroupId, itemId);
  }, [selectedGroupId, removeItemFromGroup]);



  return (
    <Grid container spacing={2} sx={{ height: '100vh' }}>
      <GroupNav 
        groups={groups}
        setSelectedGroupId={setSelectedGroupId}
      />

      <Grid item xs={12} md={9}>
        {groups.map((group) => (
          <TabPanel value={selectedGroupId} index={group._id} key={group._id} sx={{ height: '100%' }}>
            <DashboardHeader group={group} />

            <Grid container spacing={2} sx={{ height: 'fit' }}>
              <Members groupMembers={groupMembers} selectedGroupId={selectedGroupId} handleRemoveMember={handleRemoveMember} />

              <Grid item xs={12} md={6} sx={{ height: 'fit' }}>
                <Paper sx={{ p: 2, overflowY: 'auto', height: 'fit' }}>
                  <Typography variant="h6" component="div">Items</Typography>
                  <List sx={{ overflowY: 'auto', maxHeight: '300px' }}>
                    {groupItems && groupItems.map((item) => (
                      <ListItem key={item._id}>
                        <ListItemText primary={`${item.name} - $${item.price}`} />
                        <ListItemSecondaryAction>
                          <IconButton edge="end" onClick={() => handleRemoveItem(item._id)}>
                            <RemoveCircleIcon />
                          </IconButton>
                        </ListItemSecondaryAction>
                      </ListItem>
                    ))}
                  </List>
                  <Box display="flex" alignItems="center" my={2}>
                    <TextField
                      label="Item name"
                      value={newItemName}
                      onChange={(e) => setNewItemName(e.target.value)}
                    />
                    <TextField
                      label="Item price"
                      type="number"
                      value={newItemPrice}
                      onChange={(e) => setNewItemPrice(e.target.value)}
                      sx={{ ml: 2 }}
                    />
                    <Button
                      startIcon={<AddIcon />}
                      onClick={handleAddItem}
                      sx={{ ml: 2 }}
                    >
                      Add Item
                    </Button>
                  </Box>
                </Paper>
              </Grid>
            </Grid>
          </TabPanel>
        ))}
      </Grid>
    </Grid>
  );
};

const TabPanel = React.memo(function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`vertical-tabpanel-${index}`}
      aria-labelledby={`vertical-tab-${index}`}
      {...other}
      style={{ height: '100%' }}
    >
      {value === index && (
        <Box sx={{ p: 3, height: '100%' }}>
          <Typography component="div">{children}</Typography>
        </Box>
      )}
    </div>
  );
});

export default Dashboard;
