import React, { useEffect, useState } from 'react';
import { useAuthContext } from '../hooks/useAuthContext';
import { useGroupsContext } from '../hooks/useGroupContext';

const Dashboard = () => {
  const { user } = useAuthContext();
  const { groups, dispatch } = useGroupsContext();

  const [ name, setName ] = useState('')
  const [ items, setItems] = useState([])
  const [error, setError] = useState(null)


  useEffect(() => {
    const fetchGroups = async () => {
      const response = await fetch(`/api/users/${user._id}/groups/`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json'
        }
      });
      const json = await response.json();

      if (response.ok){
        dispatch({ type: 'SET_GROUPS', payload: json });
      }
    }
    fetchGroups();
  }, [dispatch])

  useEffect(() => {
    const createGroup = async () => {
      const response = await fetch(`/api/users/${user._id}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ name, items })
      });
      const json = await response.json();

      if (response.ok){
        dispatch({ type: 'CREATE_GROUP', payload: json });
      }
    }
    createGroup();
  }, [dispatch])

  handleAddItem = async (e) => {
    e.preventDefault()

    const group = {
      name,
      items
    }
    const response = await fetch(`/api/users/${user._id}`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ name, items })
    });

    const json = await response.json();

      if (response.ok){
        setName('')
        setItems([])
        console.log('new group created', json)
        dispatch({ type: 'CREATE_GROUP', payload: json });
      }
      else{
        setError(json.message)
      }

  }





  return (
    <div>
      <h1>Dashboard</h1>
      <h2>Groups</h2>
      {user && <p>User ID: {user._id}</p>}
      <ul>
        {groups && groups.length > 0 ? (
          groups.map((group) => (
            <li key={group._id}>{group.name}</li>
          ))
        ) : (
          <li>No groups available</li>
        )}
      </ul>
      <label>
        Create new group
      </label>
      <input 
        type="text"
        onChange={(e) => setName(e.target.value)}
        value={name}
      />
      <button onClick={createGroup}>Create Group</button>

    </div>
  );
};

export default Dashboard;
