import React, { useCallback } from 'react';
import { Grid, Paper, Typography, List, ListItem, ListItemText, ListItemSecondaryAction, IconButton } from '@mui/material';
import RemoveCircleIcon from '@mui/icons-material/RemoveCircle';
import AddMemberField from './AddMemberField';
import useRemoveUserFromGroup from '../hooks/useRemoveUserFromGroup';
import { useGroupsContext } from '../hooks/useGroupContext';

function Members({ groupMembers, selectedGroupId }) {
  const { removeUserFromGroup } = useRemoveUserFromGroup();
  const { dispatch } = useGroupsContext(); // Get the dispatch function from the context

  const handleAddMember = useCallback(async (newMemberEmail) => {
    try {
      const newMember = await addUsersToGroup(selectedGroupId, newMemberEmail);
      dispatch({ type: 'ADD_GROUP_MEMBER', payload: { groupId: selectedGroupId, user: newMember } });
    } catch (error) {
      console.error('Error adding member to group:', error);
    }

  }, [addUsersToGroup, selectedGroupId, dispatch]);

  const handleRemoveMember = useCallback(async (memberId) => {
    try {
      await removeUserFromGroup(selectedGroupId, memberId);
      dispatch({ type: 'REMOVE_GROUP_MEMBER', payload: { groupId: selectedGroupId, userId: memberId } });
    } catch (error) {
      console.error('Error removing member from group:', error);
    }
  }, [removeUserFromGroup, selectedGroupId, dispatch]);

  return (
    <Grid item xs={12} md={6}>
      <Paper sx={{ p: 2, overflowY: 'auto', height: 'fit' }}>
        <Typography variant="h6" component="div">Members</Typography>
        <List sx={{ overflowY: 'auto', maxHeight: '60%' }}>
          {groupMembers && groupMembers.map((member) => (
            <ListItem key={member._id}>
              <ListItemText primary={member.name} />
              <ListItemSecondaryAction>
                <IconButton edge="end" onClick={() => handleRemoveMember(member._id)}>
                  <RemoveCircleIcon />
                </IconButton>
              </ListItemSecondaryAction>
            </ListItem>
          ))}
        </List>
        <AddMemberField selectedGroupId={selectedGroupId} />
      </Paper>
    </Grid>
  );
}

export default Members;
