import React, { createContext, useReducer, useEffect } from 'react';

export const GroupsContext = createContext();

export const groupsReducer = (state, action) => {
    switch(action.type){
        case 'CREATE_GROUP':
            return { 
                groups: [...state.groups, action.payload]
            }
        case 'SET_GROUPS':
            return {
                groups: action.payload
            }
        case 'DELETE_GROUP':
            return {
                groups: state.groups.filter(group => group._id !== action.payload)
            }

        case 'ADD_GROUP_ITEM':
            return state.map(group => {
                if (group._id === action.payload.groupId) {
                    return {
                        ...group,
                        items: [...group.items, action.payload.item]
                    };
                }
                return group;
            });
        case 'REMOVE_GROUP_ITEM':
            return state.map(group => {
              if (group._id === action.payload.groupId) {
                return {
                  ...group,
                  items: group.items.filter(item => item._id !== action.payload.itemId)
                };
              }
                return group;
            });
        default:
            return state;
    }
}

export const GroupsContextProvider = ({ children }) => {
    const [groups, dispatch] = useReducer(groupsReducer, []);
    const { user } = useAuthContext();

    useEffect(() => {
      const fetchGroups = async () => {
        if (user) {
          const response = await fetch(`/api/users/${user._id}`);
          const json = await response.json();

          if (response.ok) {
            dispatch({ type: 'SET_GROUPS', payload: json.groups });
          }
        }
      };

      fetchGroups();
    }, [user]);

    return (
        <GroupsContext.Provider value={{ groups, dispatch }}>
            { children }
        </GroupsContext.Provider>
    );
}
