import React, { createContext, useReducer, useEffect } from 'react';
import { useAuthContext } from '../hooks/useAuthContext';

export const GroupsContext = createContext();

export const groupsReducer = (state, action) => {
    switch(action.type){
        case 'CREATE_GROUP':
            return { 
                groups: [...state.groups, action.payload]
            }
        case 'SET_GROUPS':
            return {
                groups: action.payload
            }
        case 'DELETE_GROUP':
            return {
                groups: state.groups.filter(group => group._id !== action.payload)
            }

        case 'ADD_GROUP_ITEM':
            return state.map(group => {
                if (group._id === action.payload.groupId) {
                    return {
                        ...group,
                        items: [...group.items, action.payload.item]
                    };
                }
                return group;
            });
        case 'REMOVE_GROUP_ITEM':
            return state.map(group => {
              if (group._id === action.payload.groupId) {
                return {
                  ...group,
                  items: group.items.filter(item => item._id !== action.payload.itemId)
                };
              }
                return group;
            });
        default:
            return state;
    }
}

export const GroupsContextProvider = ({ children }) => {
    const [groups, dispatch] = useReducer(groupsReducer, []);
    const { user } = useAuthContext();

    const [items, setItems] = useState([]);
  const [members, setMembers] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchGroupData = async () => {
      try {
        const response = await fetch(`/api/groups/${groupId}`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json'
          }
        });

        const group = await response.json();

        if (response.ok) {
          setItems(group.items);
          setMembers(group.members);
          setError(null);
        } else {
          console.error('Failed to fetch group data:', group);
          setError(group.message);
        }
      } catch (err) {
        console.error('Error fetching group data:', err);
        setError('An error occurred while fetching the group data.');
      }
    };

    if (groupId) {
      fetchGroupData();
    }
  }, [groupId]);

  return { items, members, error };

    return (
        <GroupsContext.Provider value={{ groups, dispatch }}>
            { children }
        </GroupsContext.Provider>
    );
}
