import React, { useEffect } from 'react';
import { useAuthContext } from '../hooks/useAuthContext';
import { useFetchUserGroups } from '../hooks/useFetchUserGroups';
import { useGroupsContext } from '../hooks/useGroupContext';

const Dashboard = () => {
  const { user } = useAuthContext();
  const { groups, dispatch } = useGroupsContext();

  // Custom hook to fetch user groups
  useFetchUserGroups();

  const addGroup = async (newGroup) => {
    try {
      const response = await fetch(`/api/users/${user._id}/groups/`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(newGroup)
      });

      if (!response.ok) {
        throw new Error('Failed to add group');
      }

      const addedGroup = await response.json();
      dispatch({ type: 'CREATE_GROUP', payload: addedGroup });
    } catch (error) {
      console.error('Error adding group:', error);
    }
  };

  return (
    <div>
      <h1>Dashboard</h1>
      <h2>Groups</h2>
      {user && <p>User ID: {user._id}</p>}
      <ul>
        {groups && groups.length > 0 ? (
          groups.map((group) => (
            <li key={group._id}>{group.name}</li>
          ))
        ) : (
          <li>No groups available</li>
        )}
      </ul>
      <button onClick={() => addGroup({ name: 'New Group' })}>Add Group</button>
    </div>
  );
};

export default Dashboard;
