import React, { useState, useEffect } from 'react';
import { Container, Box, Tabs, Tab, Typography, TextField, Button, List, ListItem, ListItemText, ListItemSecondaryAction, IconButton, Grid, Paper } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import AddIcon from '@mui/icons-material/Add';
import { useGroupsContext } from '../hooks/useGroupContext';
import useFetchGroups from '../hooks/useFetchUserGroups';
import useCreateGroup from '../hooks/useCreateGroup';
import useDeleteGroup from '../hooks/useDeleteGroup';
import useAddItemToGroup from '../hooks/useAddItemToGroup';
import useRemoveItemFromGroup from '../hooks/useRemoveItemFromGroup';
import useFetchGroupItems from '../hooks/useFetchGroupItems';
import { useAuthContext } from '../hooks/useAuthContext';

function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`vertical-tabpanel-${index}`}
      aria-labelledby={`vertical-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box sx={{ p: 3 }}>
          <Typography>{children}</Typography>
        </Box>
      )}
    </div>
  );
}


function a11yProps(index) {
  return {
    id: `vertical-tab-${index}`,
    'aria-controls': `vertical-tabpanel-${index}`,
  };
}

export default function GroupDashboard() {
  const { groups } = useGroupsContext();
  const { user } = useAuthContext();
  const [value, setValue] = useState(0);
  const [newItemName, setNewItemName] = useState('');
  const [newItemPrice, setNewItemPrice] = useState('');
  const [newMemberName, setNewMemberName] = useState('');
  const [items, setItems] = useState([]);
  const [selectedGroupId, setSelectedGroupId] = useState(groups.length > 0 ? groups[0]._id : null);

  useFetchGroups();
  const { createGroup, error: createGroupError } = useCreateGroup();
  const { deleteGroup, error: deleteGroupError } = useDeleteGroup();
  const { addItemToGroup } = useAddItemToGroup();
  const { removeItemFromGroup } = useRemoveItemFromGroup();

  const { items: groupItems, members: groupMembers, error: fetchGroupItemsError } = useFetchGroupItems(selectedGroupId);

  const handleAddItem = async (groupId) => {
    const result = await addItemToGroup(groupId, newItemName, newItemPrice);

    if (result) {
      setItems((prevItems) => [...prevItems, result]);
      setNewItemName('');
      setNewItemPrice('');
    }
  };

  const handleRemoveItem = async (groupId, itemId) => {
    const result = await removeItemFromGroup(groupId, itemId);

    if (result) {
      setItems((prevItems) => prevItems.filter((item) => item._id !== itemId));
    }
  };

  const handleAddMember = (groupId) => {
    // Logic to add member to group
  };

  const handleRemoveMember = (groupId, memberId) => {
    // Logic to remove member from group
  };

  const handleChange = (event, newValue) => {
    setValue(newValue);
    setSelectedGroupId(groups[newValue]._id);
  };

  return (
    <Container>
      <Box sx={{ flexGrow: 1, bgcolor: 'background.paper', display: 'flex', height: 'auto' }}>
        <Tabs
          orientation="vertical"
          variant="scrollable"
          value={value}
          onChange={handleChange}
          aria-label="Group tabs"
          sx={{ 
            borderRight: 1, 
            borderColor: 'divider', 
            minWidth: 'fit-content',
            '& .MuiTab-root': { fontSize: '1.1rem' } 
          }}
        >
          {groups.map((group, index) => (
            <Tab label={group.name} {...a11yProps(index)} key={group._id} />
          ))}
        </Tabs>
        {groups.map((group, index) => (
          <TabPanel value={value} index={index} key={group._id}>
            <Grid container spacing={2}>
              <Grid item xs={12}>
                <Paper sx={{ p: 2 }}>
                  <Typography variant="h6">{group.name}</Typography>
                  <Typography variant="subtitle1">Amount you owe: $XX.XX</Typography>
                </Paper>
              </Grid>

              <Grid item xs={12} md={6}>
                <Paper sx={{ p: 2 }}>
                  <Typography variant="h6">Members</Typography>
                  <List>
                    {groupMembers && groupMembers.map((member) => (
                      <ListItem key={member._id}>
                        <ListItemText primary={member.name} />
                        <ListItemSecondaryAction>
                          <IconButton edge="end" onClick={() => handleRemoveMember(group._id, member._id)}>
                            <DeleteIcon />
                          </IconButton>
                        </ListItemSecondaryAction>
                      </ListItem>
                    ))}
                  </List>
                  <Box display="flex" alignItems="center" my={2}>
                    <TextField
                      label="New member name"
                      value={newMemberName}
                      onChange={(e) => setNewMemberName(e.target.value)}
                    />
                    <Button
                      startIcon={<AddIcon />}
                      onClick={() => handleAddMember(group._id)}
                      sx={{ ml: 2 }}
                    >
                      Add Member
                    </Button>
                  </Box>
                </Paper>
              </Grid>

              <Grid item xs={12} md={6}>
                <Paper sx={{ p: 2 }}>
                  <Typography variant="h6">Items</Typography>
                  <List>
                    {groupItems && groupItems.map((item) => (
                      <ListItem key={item._id}>
                        <ListItemText primary={`${item.name} - $${item.price}`} />
                        <ListItemSecondaryAction>
                          <IconButton edge="end" onClick={() => handleRemoveItem(group._id, item._id)}>
                            <DeleteIcon />
                          </IconButton>
                        </ListItemSecondaryAction>
                      </ListItem>
                    ))}
                  </List>
                  <Box display="flex" alignItems="center" my={2}>
                    <TextField
                      label="Item name"
                      value={newItemName}
                      onChange={(e) => setNewItemName(e.target.value)}
                    />
                    <TextField
                      label="Item price"
                      type="number"
                      value={newItemPrice}
                      onChange={(e) => setNewItemPrice(e.target.value)}
                      sx={{ ml: 2 }}
                    />
                    <Button
                      startIcon={<AddIcon />}
                      onClick={() => handleAddItem(group._id)}
                      sx={{ ml: 2 }}
                    >
                      Add Item
                    </Button>
                  </Box>
                </Paper>
              </Grid>
            </Grid>
          </TabPanel>
        ))}
      </Box>
    </Container>
  );
}
