import React, { useEffect, useState } from 'react';
import { Paper, Typography } from '@mui/material';
import useFetchGroupItems from '../hooks/useFetchGroupItems';

function DashboardHeader({ group }) {
  const { members, items, total, fetchGroupItems } = useFetchGroupItems();
  const [splitAmount, setSplitAmount] = useState(0);

  useEffect(() => {
    if (group._id) {
      fetchGroupItems(group._id);
    }
  }, [group._id, fetchGroupItems]);

  useEffect(() => {
    // Calculate split amount per member
    const split = members.length > 0 ? total / members.length : 0;
    setSplitAmount(split);
  }, [total, members]);

  return (
    <Paper sx={{ p: 2, mb: 4 }}>
      <Typography variant="h4" component="div" sx={{ fontWeight: 'bold' }}>
        {group.name}
      </Typography>
      <Typography variant="subtitle1" component="div">
        Amount you owe: ${splitAmount.toFixed(2)}
      </Typography>
    </Paper>
  );
}

export default DashboardHeader;
